package menu.controller;import java.util.List;import menu.domain.Coach;import menu.service.CoachService;import menu.view.InputView;public class CoachController {    private final InputView inputView;    private final CoachService coachService;    public CoachController(InputView inputView, CoachService coachService) {        this.inputView = inputView;        this.coachService = coachService;    }    public void init() {        String coachNames = inputView.readCoachInput();        coachService.addCoaches(coachNames);    }    public void initCoachFood() {        List<String> coachNames = coachService.coachNames();        for (String coachName : coachNames) {            String inputMenus = inputView.readCoachFoodInput(coachName);            coachService.addUnEatableMenu(inputMenus, coachName);        }    }    public void addEatFood(String menu, int count) {        coachService.addEatMenu(menu, count);    }    public boolean success(int count) {        return coachService.success(count);    }    public List<Coach> getCoaches() {        return coachService.getCoaches();    }}