package menu.domain;import java.util.ArrayList;import java.util.List;import java.util.stream.Collectors;public class Coach {    private final String name;    private final List<Menu> unEatableMenu = new ArrayList<>();    private final List<Menu> eatMenu = new ArrayList<>();    int eatCount = 0;    public Coach(String name) {        this.name = name;    }    public void addUnEatableMenu(String menu) {        unEatableMenu.add(new Menu(menu));    }    public void addEatMenu(String menu) {        eatMenu.add(new Menu(menu));    }    public boolean equalsByName(String name) {        if (this.name.equals(name)) {            return true;        }        return false;    }    public List<String> eatMenus() {        return eatMenu.stream().map(menu -> menu.toString()).collect(Collectors.toList());    }    public String getCoachName() {        return name;    }    public boolean enoughMenu(int count) {        if (eatCount != count) {            return false;        }        return true;    }    public void addCount() {        eatCount++;    }}