package menu.repository;import java.util.ArrayList;import java.util.Collections;import java.util.List;import menu.domain.Category;public class CategoryRepository {    private final List<Category> categories = new ArrayList<>();    private final List<Category> selectedCategories = new ArrayList<>();    public void addCategory(Category category) {        categories.add(category);    }    public List<Category> getCategory() {        return Collections.unmodifiableList(categories);    }    public void addSelectedCategory(Category category) {        selectedCategories.add(category);    }    public List<Category> getSelectedCategory() {        return Collections.unmodifiableList(selectedCategories);    }    public boolean checkDoubleDuplicateCategory(Category targetCategory) {        long count = selectedCategories.stream().filter(category ->                category.equals(targetCategory)).count();        if (count >= 2) {            return true;        }        return false;    }}