package menu.repository;import java.util.ArrayList;import java.util.Collection;import java.util.Collections;import java.util.HashMap;import java.util.List;import java.util.Map;import menu.domain.Category;import menu.domain.Menu;public class MenuRepository {    public final Map<Category, List<Menu>> menus = new HashMap<>();    public MenuRepository() {}    public void addMenu(Category category, Menu menu) {        if (!menus.containsKey(category)) {            menus.put(category, new ArrayList<>());            menus.get(category).add(menu);            return;        }        menus.get(category).add(menu);    }    public List<Menu> findByCategory(Category category) {        return Collections.unmodifiableList(menus.get(category));    }}