package menu.service;import camp.nextstep.edu.missionutils.Randoms;import java.util.ArrayList;import java.util.List;import java.util.stream.Collectors;import menu.domain.Category;import menu.domain.Menu;import menu.repository.MenuRepository;import menu.utils.InitData;public class MenuService {    private final MenuRepository menuRepository;    private final CategoryService categoryService;    public MenuService(CategoryService categoryService) {        this.menuRepository = new MenuRepository();        this.categoryService = categoryService;    }    public void initMenu() {        List<Category> categories = categoryService.getCategory();        List<List<String>> menus = InitData.menus;        for (int i = 0; i < categories.size(); i++) {            Category category = categories.get(i);            List<String> menu = menus.get(i);            addMenuToCategory(category, menu);        }    }    public void addMenuToCategory(Category category, List<String> menus) {        for (String menu : menus) {            menuRepository.addMenu(category, new Menu(menu));        }    }    public String recommend(Category category) {        List<String> menus = menuRepository.findByCategory(category).stream().map(menu -> menu.toString())            .collect(Collectors.toCollection(ArrayList::new));        String menu = Randoms.shuffle(menus).get(0);        return menu;    }}